import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

function App() {
  const [gameState, setGameState] = useState('lobby');
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);
  const [player, setPlayer] = useState(null);
  const [room, setRoom] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [mode, setMode] = useState('create');
  
  // Game state
  const [gameData, setGameData] = useState(null);
  const [myCards, setMyCards] = useState([]);
  const [isMyTurn, setIsMyTurn] = useState(false);

  // Socket connection
  useEffect(() => {
    console.log('üîó Initializing socket connection...');
    
    const socketInstance = io('http://localhost:3003', {
      transports: ['websocket', 'polling']
    });

    socketInstance.on('connect', () => {
      setConnected(true);
      console.log('‚úÖ Connected to server');
    });

    socketInstance.on('disconnect', () => {
      setConnected(false);
      console.log('‚ùå Disconnected from server');
    });

    socketInstance.on('roomCreated', (data) => {
      console.log('üè† Room created:', data);
      setRoom(data.room);
      setPlayer(data.room.players[0]);
      setGameState('waiting');
    });

    socketInstance.on('roomJoined', (data) => {
      console.log('üö™ Room joined:', data);
      setRoom(data.room);
      setPlayer(data.player);
      setGameState('waiting');
    });

    socketInstance.on('playerJoined', (data) => {
      console.log('üë§ Player joined:', data);
      setRoom(prevRoom => ({
        ...prevRoom,
        players: [...(prevRoom?.players || []), data.player]
      }));
    });

    socketInstance.on('gameStarted', (data) => {
      console.log('üéÆ Game started:', data);
      setGameState('playing');
    });

    socketInstance.on('gameUpdate', (data) => {
      console.log('üìä Game update received:', data);
      setGameData(data);
      setMyCards(data.myCards || []);
      setIsMyTurn(data.isMyTurn || false);
    });

    socketInstance.on('gameError', (error) => {
      console.error('‚ùå Game error:', error);
      alert(error.message);
    });

    socketInstance.on('gameWon', (data) => {
      console.log('üèÜ Game won:', data);
      alert(`üéâ ${data.winner} wins!`);
      setTimeout(() => {
        setGameState('lobby');
        setRoom(null);
        setPlayer(null);
        setGameData(null);
        setMyCards([]);
      }, 3000);
    });

    socketInstance.on('error', (error) => {
      console.error('‚ùå Socket error:', error);
      alert(error.message);
    });

    setSocket(socketInstance);

    return () => {
      console.log('üîå Closing socket connection');
      socketInstance.close();
    };
  }, []);

  const createRoom = () => {
    if (socket && playerName.trim()) {
      console.log('üéØ Creating room for:', playerName);
      socket.emit('createRoom', { playerName: playerName.trim() });
    }
  };

  const joinRoom = () => {
    if (socket && playerName.trim() && roomId.trim()) {
      console.log('üéØ Joining room:', roomId);
      socket.emit('joinRoom', { 
        roomId: roomId.trim().toUpperCase(), 
        playerName: playerName.trim() 
      });
    }
  };

  const playCard = (cardId) => {
    if (socket && isMyTurn) {
      console.log('üÉè Playing card:', cardId);
      socket.emit('playCard', { cardId });
    } else {
      alert('Not your turn!');
    }
  };

  const drawCard = () => {
    if (socket && isMyTurn) {
      console.log('üÉè Drawing card');
      socket.emit('drawCard');
    } else {
      alert('Not your turn!');
    }
  };

  // Debug info
  console.log('=== DEBUG INFO ===');
  console.log('Game State:', gameState);
  console.log('Connected:', connected);
  console.log('My Cards:', myCards);
  console.log('Is My Turn:', isMyTurn);
  console.log('Game Data:', gameData);
  console.log('==================');

  const styles = {
    body: {
      fontFamily: 'Arial, sans-serif',
      margin: 0,
      background: 'linear-gradient(135deg, #1a1a2e, #16213e, #0f3460)',
      color: 'white',
      minHeight: '100vh'
    },
    container: {
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto'
    },
    lobby: {
      textAlign: 'center',
      padding: '50px',
      background: 'rgba(255,255,255,0.1)',
      borderRadius: '20px',
      marginTop: '50px'
    },
    input: {
      padding: '15px',
      fontSize: '1.1em',
      border: 'none',
      borderRadius: '10px',
      margin: '10px',
      width: '200px'
    },
    button: {
      padding: '15px 25px',
      fontSize: '1.1em',
      border: 'none',
      borderRadius: '10px',
      margin: '10px',
      cursor: 'pointer',
      background: '#ff4757',
      color: 'white'
    },
    gameBoard: {
      textAlign: 'center',
      padding: '20px'
    },
    players: {
      display: 'flex',
      gap: '20px',
      marginBottom: '30px',
      justifyContent: 'center',
      flexWrap: 'wrap'
    },
    player: {
      padding: '15px 25px',
      background: 'rgba(255,255,255,0.2)',
      borderRadius: '15px',
      border: '3px solid transparent'
    },
    currentPlayer: {
      background: '#ffd700',
      color: '#000',
      borderColor: '#ffaa00'
    },
    centerArea: {
      display: 'flex',
      gap: '40px',
      justifyContent: 'center',
      margin: '30px 0'
    },
    card: {
      width: '120px',
      height: '180px',
      borderRadius: '15px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '1.5em',
      fontWeight: 'bold',
      color: 'white',
      cursor: 'pointer',
      border: '3px solid #fff',
      boxShadow: '0 8px 25px rgba(0,0,0,0.3)'
    },
    playerHand: {
      position: 'fixed',
      bottom: '20px',
      left: '50%',
      transform: 'translateX(-50%)',
      display: 'flex',
      gap: '10px',
      padding: '20px',
      background: 'rgba(255,255,255,0.1)',
      borderRadius: '20px',
      maxWidth: '90vw',
      overflowX: 'auto'
    },
    handCard: {
      width: '80px',
      height: '120px',
      borderRadius: '10px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 'bold',
      color: 'white',
      cursor: 'pointer',
      border: '2px solid #fff',
      minWidth: '80px',
      fontSize: '0.9em'
    },
    turnIndicator: {
      fontSize: '1.5em',
      fontWeight: 'bold',
      margin: '20px 0',
      padding: '20px',
      background: 'rgba(255,255,255,0.1)',
      borderRadius: '15px'
    },
    myTurn: {
      background: '#2ed573'
    },
    connectionStatus: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '10px',
      marginBottom: '20px'
    },
    statusDot: {
      width: '12px',
      height: '12px',
      borderRadius: '50%',
      background: connected ? '#2ed573' : '#ff4757'
    }
  };

  const getCardColor = (color) => {
    switch(color) {
      case 'red': return '#ff4757';
      case 'blue': return '#3742fa';
      case 'green': return '#2ed573';
      case 'yellow': return '#ffa502';
      default: return '#57606f';
    }
  };

  if (gameState === 'lobby') {
    return (
      <div style={styles.body}>
        <div style={styles.container}>
          <div style={styles.lobby}>
            <h1 style={{ fontSize: '4em', marginBottom: '20px' }}>üÉè UNO</h1>
            <p style={{ fontSize: '1.2em', marginBottom: '30px' }}>Real-time Multiplayer Card Game</p>
            
            <div style={styles.connectionStatus}>
              <div style={styles.statusDot}></div>
              {connected ? 'Connected to server' : 'Connecting...'}
            </div>

            <div>
              <input 
                style={styles.input}
                type="text" 
                placeholder="Enter your name..." 
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                maxLength={20}
              />
              {mode === 'join' && (
                <input 
                  style={styles.input}
                  type="text" 
                  placeholder="Room ID..." 
                  value={roomId}
                  onChange={(e) => setRoomId(e.target.value.toUpperCase())}
                  maxLength={6}
                />
              )}
            </div>
            
            <div>
              <button 
                style={styles.button}
                onClick={() => {
                  setMode('create');
                  if (playerName.trim()) createRoom();
                }}
                disabled={!playerName.trim() || !connected}
              >
                ‚ûï Create Room
              </button>
              <button 
                style={styles.button}
                onClick={() => {
                  if (mode === 'join') {
                    if (playerName.trim() && roomId.trim()) joinRoom();
                  } else {
                    setMode('join');
                  }
                }}
              >
                üö™ {mode === 'join' ? 'Join Room' : 'Join Existing'}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'waiting') {
    return (
      <div style={styles.body}>
        <div style={styles.container}>
          <div style={styles.lobby}>
            <h1 style={{ fontSize: '3em' }}>üè† Room: {room?.id}</h1>
            <p style={{ fontSize: '1.2em', marginBottom: '30px' }}>Share this room ID with your friends!</p>
            
            <div style={{ marginBottom: '30px' }}>
              <h3>Players ({room?.players?.length || 0}/4):</h3>
              {room?.players?.map((roomPlayer, index) => (
                <div key={index} style={{
                  display: 'inline-block',
                  margin: '10px',
                  padding: '10px 15px',
                  background: roomPlayer.id === player?.id ? '#ff4757' : 'rgba(255,255,255,0.2)',
                  borderRadius: '20px',
                  fontSize: '1.1em'
                }}>
                  {roomPlayer.name} {roomPlayer.id === player?.id && '(You)'}
                </div>
              ))}
            </div>

            <p style={{ fontSize: '1.1em', opacity: 0.8 }}>
              {room?.players?.length < 2 ? 
                `Waiting for more players... (${room?.players?.length}/4)` :
                '‚ö° Game starting soon!'
              }
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'playing') {
    return (
      <div style={styles.body}>
        <div style={styles.container}>
          <div style={styles.gameBoard}>
            {/* Players */}
            <div style={styles.players}>
              {gameData?.players?.map((playerData, index) => (
                <div 
                  key={index} 
                  style={{
                    ...styles.player,
                    ...(playerData.isCurrentPlayer ? styles.currentPlayer : {})
                  }}
                >
                  <div style={{ fontWeight: 'bold' }}>{playerData.name}</div>
                  <div>{playerData.cardCount} cards</div>
                </div>
              ))}
            </div>

            {/* Game Center */}
            <div style={styles.centerArea}>
              <div style={{ textAlign: 'center' }}>
                <div 
                  style={{...styles.card, background: '#57606f'}}
                  onClick={drawCard}
                >
                  üÉè
                </div>
                <div>Draw ({gameData?.deckSize || 0})</div>
              </div>

              <div style={{ textAlign: 'center' }}>
                <div 
                  style={{
                    ...styles.card, 
                    background: getCardColor(gameData?.topCard?.color)
                  }}
                >
                  {gameData?.topCard?.value?.toUpperCase() || '?'}
                </div>
                <div>Top Card</div>
              </div>
            </div>

            {/* Turn Indicator */}
            <div style={{
              ...styles.turnIndicator,
              ...(isMyTurn ? styles.myTurn : {})
            }}>
              {isMyTurn ? 'üéØ Your Turn!' : `Waiting for ${gameData?.currentPlayerName || 'player'}...`}
            </div>
          </div>

          {/* Player Hand */}
          <div style={styles.playerHand}>
            {myCards.length > 0 ? (
              myCards.map((card) => (
                <div
                  key={card.id}
                  style={{
                    ...styles.handCard,
                    background: getCardColor(card.color)
                  }}
                  onClick={() => playCard(card.id)}
                >
                  {card.value}
                </div>
              ))
            ) : (
              <div style={{ padding: '20px', opacity: 0.7 }}>
                No cards yet...
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.body}>
      <div style={styles.container}>
        <div style={{ textAlign: 'center', padding: '50px' }}>
          <h2>üéÆ Loading UNO Game...</h2>
          <p>Current state: {gameState}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
