import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

function App() {
  // KEEP ALL EXISTING NETWORK STATE (unchanged)
  const [gameState, setGameState] = useState('lobby');
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);
  const [player, setPlayer] = useState(null);
  const [room, setRoom] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [mode, setMode] = useState('create');
  const [gameData, setGameData] = useState(null);
  const [myCards, setMyCards] = useState([]);
  const [isMyTurn, setIsMyTurn] = useState(false);

  // NEW UI STATE
  const [theme, setTheme] = useState('dark');
  const [showChat, setShowChat] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [pendingWildCard, setPendingWildCard] = useState(null);

  // KEEP ALL EXISTING SOCKET CODE (unchanged)
  useEffect(() => {
    console.log('🔗 Initializing socket connection...');
    
    const socketInstance = io('http://localhost:3003', {
      transports: ['websocket', 'polling']
    });

    socketInstance.on('connect', () => {
      setConnected(true);
      console.log('✅ Connected to server');
    });

    socketInstance.on('disconnect', () => {
      setConnected(false);
      console.log('❌ Disconnected from server');
    });

    socketInstance.on('roomCreated', (data) => {
      console.log('🏠 Room created:', data);
      setRoom(data.room);
      setPlayer(data.room.players[0]);
      setGameState('waiting');
    });

    socketInstance.on('roomJoined', (data) => {
      console.log('🚪 Room joined:', data);
      setRoom(data.room);
      setPlayer(data.player);
      setGameState('waiting');
    });

    socketInstance.on('playerJoined', (data) => {
      console.log('👤 Player joined:', data);
      setRoom(prevRoom => ({
        ...prevRoom,
        players: [...(prevRoom?.players || []), data.player]
      }));
    });

    socketInstance.on('gameStarted', (data) => {
      console.log('🎮 Game started:', data);
      setGameState('playing');
    });

    socketInstance.on('gameUpdate', (data) => {
      console.log('📊 Game update received:', data);
      setGameData(data);
      setMyCards(data.myCards || []);
      setIsMyTurn(data.isMyTurn || false);
    });

    socketInstance.on('gameError', (error) => {
      console.error('❌ Game error:', error);
      alert(error.message);
    });

    socketInstance.on('gameWon', (data) => {
      console.log('🏆 Game won:', data);
      alert(`🎉 ${data.winner} wins!`);
      setTimeout(() => {
        setGameState('lobby');
        setRoom(null);
        setPlayer(null);
        setGameData(null);
        setMyCards([]);
        setChatMessages([]);
      }, 3000);
    });

    // NEW: Chat message handler
    socketInstance.on('chatMessage', (message) => {
      setChatMessages(prev => [...prev, {
        player: message.player,
        message: message.message,
        timestamp: new Date().toLocaleTimeString()
      }]);
    });

    socketInstance.on('error', (error) => {
      console.error('❌ Socket error:', error);
      alert(error.message);
    });

    setSocket(socketInstance);

    return () => {
      console.log('🔌 Closing socket connection');
      socketInstance.close();
    };
  }, []);

  // KEEP ALL EXISTING FUNCTIONS (unchanged)
  const createRoom = () => {
    if (socket && playerName.trim()) {
      console.log('🎯 Creating room for:', playerName);
      socket.emit('createRoom', { playerName: playerName.trim() });
    }
  };

  const joinRoom = () => {
    if (socket && playerName.trim() && roomId.trim()) {
      console.log('🎯 Joining room:', roomId);
      socket.emit('joinRoom', { 
        roomId: roomId.trim().toUpperCase(), 
        playerName: playerName.trim() 
      });
    }
  };

  const playCard = (cardId, chosenColor = null) => {
    if (socket && isMyTurn) {
      console.log('🃏 Playing card:', cardId);
      socket.emit('playCard', { cardId, chosenColor });
    } else {
      alert('Not your turn!');
    }
  };

  const drawCard = () => {
    if (socket && isMyTurn) {
      console.log('🃏 Drawing card');
      socket.emit('drawCard');
    } else {
      alert('Not your turn!');
    }
  };

  // NEW FUNCTIONS
  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  const sendChatMessage = () => {
    if (socket && chatInput.trim()) {
      socket.emit('sendMessage', { 
        player: player?.name || 'Unknown',
        message: chatInput.trim() 
      });
      setChatInput('');
    }
  };

  const handleCardClick = (card) => {
    if (!isMyTurn) {
      alert('Not your turn!');
      return;
    }

    if (card.color === 'wild') {
      setPendingWildCard(card);
      setShowColorPicker(true);
    } else {
      playCard(card.id);
    }
  };

  const handleColorChoice = (color) => {
    if (pendingWildCard) {
      playCard(pendingWildCard.id, color);
      setPendingWildCard(null);
      setShowColorPicker(false);
    }
  };

  // ENHANCED THEMES
  const themes = {
    dark: {
      background: 'linear-gradient(135deg, #0f0f23 0%, #1a1a2e 50%, #16213e 100%)',
      cardBg: 'rgba(255, 255, 255, 0.1)',
      text: '#ffffff',
      textSecondary: '#b8b8b8',
      border: 'rgba(255, 255, 255, 0.2)',
      hover: 'rgba(255, 255, 255, 0.2)',
      success: '#10b981',
      error: '#ef4444',
      warning: '#f59e0b',
      accent: '#8b5cf6',
      chatBg: 'rgba(0, 0, 0, 0.3)'
    },
    light: {
      background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 50%, #cbd5e1 100%)',
      cardBg: 'rgba(255, 255, 255, 0.9)',
      text: '#1f2937',
      textSecondary: '#6b7280',
      border: 'rgba(0, 0, 0, 0.1)',
      hover: 'rgba(0, 0, 0, 0.05)',
      success: '#059669',
      error: '#dc2626',
      warning: '#d97706',
      accent: '#7c3aed',
      chatBg: 'rgba(255, 255, 255, 0.9)'
    }
  };

  const currentTheme = themes[theme];

  const styles = {
    app: {
      fontFamily: "'Inter', 'Segoe UI', system-ui, sans-serif",
      margin: 0,
      background: currentTheme.background,
      color: currentTheme.text,
      minHeight: '100vh',
      position: 'relative',
      transition: 'all 0.3s ease'
    },
    container: {
      padding: '20px',
      maxWidth: '1400px',
      margin: '0 auto',
      position: 'relative'
    },
    themeToggle: {
      position: 'fixed',
      top: '20px',
      right: '20px',
      background: currentTheme.cardBg,
      border: `1px solid ${currentTheme.border}`,
      borderRadius: '12px',
      padding: '12px',
      cursor: 'pointer',
      zIndex: 1000,
      backdropFilter: 'blur(10px)',
      fontSize: '1.5em',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)'
    },
    chatToggle: {
      position: 'fixed',
      top: '80px',
      right: '20px',
      background: currentTheme.accent,
      color: 'white',
      border: 'none',
      borderRadius: '12px',
      padding: '12px',
      cursor: 'pointer',
      zIndex: 1000,
      fontSize: '1.2em',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)'
    },
    lobby: {
      textAlign: 'center',
      padding: '60px',
      background: currentTheme.cardBg,
      borderRadius: '24px',
      marginTop: '80px',
      backdropFilter: 'blur(20px)',
      border: `1px solid ${currentTheme.border}`,
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.1)'
    },
    title: {
      fontSize: '5em',
      fontWeight: '800',
      marginBottom: '10px',
      background: 'linear-gradient(135deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      letterSpacing: '-2px'
    },
    subtitle: {
      fontSize: '1.5em',
      color: currentTheme.textSecondary,
      marginBottom: '40px',
      fontWeight: '300'
    },
    input: {
      padding: '18px 24px',
      fontSize: '1.2em',
      border: `2px solid ${currentTheme.border}`,
      borderRadius: '16px',
      margin: '10px',
      width: '280px',
      background: currentTheme.cardBg,
      color: currentTheme.text,
      backdropFilter: 'blur(10px)',
      transition: 'all 0.3s ease'
    },
    button: {
      padding: '18px 32px',
      fontSize: '1.2em',
      border: 'none',
      borderRadius: '16px',
      margin: '10px',
      cursor: 'pointer',
      background: 'linear-gradient(135deg, #ff6b6b, #ee5a52)',
      color: 'white',
      fontWeight: '600',
      transition: 'all 0.3s ease',
      boxShadow: '0 8px 25px rgba(255, 107, 107, 0.3)',
      transform: 'translateY(0)'
    },
    buttonSecondary: {
      background: 'linear-gradient(135deg, #667eea, #764ba2)',
      boxShadow: '0 8px 25px rgba(102, 126, 234, 0.3)'
    },
    gameBoard: {
      textAlign: 'center',
      padding: '20px',
      minHeight: '100vh'
    },
    gameHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '25px',
      background: currentTheme.cardBg,
      borderRadius: '20px',
      marginBottom: '30px',
      backdropFilter: 'blur(20px)',
      border: `1px solid ${currentTheme.border}`,
      boxShadow: '0 10px 40px rgba(0, 0, 0, 0.1)'
    },
    players: {
      display: 'flex',
      gap: '20px',
      flexWrap: 'wrap'
    },
    player: {
      padding: '20px 25px',
      background: currentTheme.cardBg,
      borderRadius: '16px',
      border: `2px solid ${currentTheme.border}`,
      backdropFilter: 'blur(15px)',
      transition: 'all 0.3s ease',
      minWidth: '140px'
    },
    currentPlayer: {
      background: 'linear-gradient(135deg, #ffd700, #ffed4e)',
      color: '#000',
      border: '2px solid #ffa500',
      boxShadow: '0 10px 30px rgba(255, 215, 0, 0.4)',
      transform: 'scale(1.05)'
    },
    centerArea: {
      display: 'flex',
      gap: '60px',
      justifyContent: 'center',
      margin: '40px 0',
      alignItems: 'center'
    },
    card: {
      width: '140px',
      height: '200px',
      borderRadius: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '2em',
      fontWeight: 'bold',
      color: 'white',
      cursor: 'pointer',
      border: '4px solid rgba(255, 255, 255, 0.3)',
      boxShadow: '0 15px 40px rgba(0, 0, 0, 0.2)',
      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      position: 'relative',
      overflow: 'hidden'
    },
    cardShine: {
      position: 'absolute',
      top: 0,
      left: '-100%',
      width: '100%',
      height: '100%',
      background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent)',
      transition: 'left 0.5s ease'
    },
    playerHand: {
      position: 'fixed',
      bottom: '20px',
      left: '50%',
      transform: 'translateX(-50%)',
      display: 'flex',
      gap: '12px',
      padding: '25px',
      background: currentTheme.cardBg,
      borderRadius: '25px',
      maxWidth: '95vw',
      overflowX: 'auto',
      backdropFilter: 'blur(25px)',
      border: `1px solid ${currentTheme.border}`,
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.2)',
      zIndex: 100
    },
    handCard: {
      width: '90px',
      height: '130px',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 'bold',
      color: 'white',
      cursor: 'pointer',
      border: '3px solid rgba(255, 255, 255, 0.4)',
      minWidth: '90px',
      fontSize: '1em',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      boxShadow: '0 8px 25px rgba(0, 0, 0, 0.15)',
      position: 'relative',
      overflow: 'hidden'
    },
    turnIndicator: {
      fontSize: '2em',
      fontWeight: '700',
      margin: '30px 0',
      padding: '25px',
      background: currentTheme.cardBg,
      borderRadius: '20px',
      backdropFilter: 'blur(20px)',
      border: `1px solid ${currentTheme.border}`,
      boxShadow: '0 10px 40px rgba(0, 0, 0, 0.1)',
      transition: 'all 0.3s ease'
    },
    myTurn: {
      background: 'linear-gradient(135deg, #10b981, #059669)',
      color: 'white',
      border: '2px solid #047857',
      boxShadow: '0 15px 50px rgba(16, 185, 129, 0.3)',
      animation: 'pulse 2s infinite'
    },
    connectionStatus: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '12px',
      marginBottom: '25px',
      fontSize: '1.1em'
    },
    statusDot: {
      width: '16px',
      height: '16px',
      borderRadius: '50%',
      background: connected ? currentTheme.success : currentTheme.error,
      boxShadow: `0 0 20px ${connected ? currentTheme.success : currentTheme.error}`,
      animation: connected ? 'none' : 'pulse 1.5s infinite'
    },
    // Chat Styles
    chatContainer: {
      position: 'fixed',
      top: '20px',
      right: showChat ? '20px' : '-350px',
      width: '320px',
      height: '500px',
      background: currentTheme.chatBg,
      borderRadius: '20px',
      border: `1px solid ${currentTheme.border}`,
      backdropFilter: 'blur(25px)',
      display: 'flex',
      flexDirection: 'column',
      zIndex: 999,
      transition: 'right 0.3s ease',
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.2)'
    },
    chatHeader: {
      padding: '20px',
      borderBottom: `1px solid ${currentTheme.border}`,
      fontWeight: '600',
      fontSize: '1.2em',
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    chatMessages: {
      flex: 1,
      padding: '15px',
      overflowY: 'auto',
      fontSize: '0.9em'
    },
    chatMessage: {
      marginBottom: '12px',
      padding: '10px 12px',
      background: currentTheme.cardBg,
      borderRadius: '12px',
      backdropFilter: 'blur(10px)'
    },
    chatInput: {
      display: 'flex',
      padding: '15px',
      gap: '10px',
      borderTop: `1px solid ${currentTheme.border}`
    },
    chatInputField: {
      flex: 1,
      padding: '12px',
      border: `1px solid ${currentTheme.border}`,
      borderRadius: '12px',
      background: currentTheme.cardBg,
      color: currentTheme.text,
      fontSize: '0.9em'
    },
    chatSendBtn: {
      padding: '12px 16px',
      border: 'none',
      borderRadius: '12px',
      background: currentTheme.accent,
      color: 'white',
      cursor: 'pointer',
      fontSize: '1.1em'
    },
    // Color Picker
    colorPicker: {
      position: 'fixed',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      background: currentTheme.cardBg,
      padding: '40px',
      borderRadius: '24px',
      border: `1px solid ${currentTheme.border}`,
      backdropFilter: 'blur(30px)',
      zIndex: 2000,
      textAlign: 'center',
      boxShadow: '0 30px 80px rgba(0, 0, 0, 0.3)'
    },
    colorOption: {
      width: '80px',
      height: '80px',
      borderRadius: '50%',
      cursor: 'pointer',
      margin: '10px',
      display: 'inline-flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '1.2em',
      fontWeight: 'bold',
      color: 'white',
      border: '4px solid rgba(255, 255, 255, 0.5)',
      transition: 'all 0.3s ease',
      boxShadow: '0 8px 25px rgba(0, 0, 0, 0.2)'
    }
  };

  const getCardColor = (color) => {
    const cardColors = {
      red: 'linear-gradient(135deg, #ff6b6b, #ee5a52)',
      blue: 'linear-gradient(135deg, #4dabf7, #339af0)',
      green: 'linear-gradient(135deg, #51cf66, #40c057)',
      yellow: 'linear-gradient(135deg, #ffd43b, #fab005)',
      wild: 'linear-gradient(135deg, #9775fa, #8c7ae6, #7048e8, #6c5ce7)'
    };
    return cardColors[color] || 'linear-gradient(135deg, #868e96, #6c757d)';
  };

  // Add CSS animations
  const cssAnimations = `
    @keyframes pulse {
      0%, 100% { opacity: 1; transform: scale(1); }
      50% { opacity: 0.8; transform: scale(1.05); }
    }
    
    @keyframes cardHover {
      0% { transform: translateY(0) rotateY(0); }
      100% { transform: translateY(-20px) rotateY(10deg); }
    }
    
    .card-hover:hover {
      animation: cardHover 0.3s ease;
      box-shadow: 0 25px 60px rgba(0, 0, 0, 0.4) !important;
    }
    
    .hand-card:hover {
      transform: translateY(-15px) scale(1.1) rotateZ(5deg) !important;
      z-index: 10;
      box-shadow: 0 20px 50px rgba(0, 0, 0, 0.3) !important;
    }
    
    .button:hover {
      transform: translateY(-3px) !important;
      box-shadow: 0 12px 35px rgba(0, 0, 0, 0.2) !important;
    }
    
    .theme-toggle:hover {
      transform: scale(1.1) rotate(180deg) !important;
    }
  `;

  // Insert CSS
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = cssAnimations;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  console.log('=== DEBUG INFO ===');
  console.log('Game State:', gameState);
  console.log('Connected:', connected);
  console.log('My Cards:', myCards);
  console.log('Is My Turn:', isMyTurn);
  console.log('Game Data:', gameData);
  console.log('==================');

  if (gameState === 'lobby') {
    return (
      <div style={styles.app}>
        <div 
          style={styles.themeToggle} 
          className="theme-toggle"
          onClick={toggleTheme}
        >
          {theme === 'dark' ? '🌞' : '🌙'}
        </div>
        
        <div style={styles.container}>
          <div style={styles.lobby}>
            <h1 style={styles.title}>🃏 UNO</h1>
            <p style={styles.subtitle}>Real-time Multiplayer Card Game Experience</p>
            
            <div style={styles.connectionStatus}>
              <div style={styles.statusDot}></div>
              {connected ? 'Connected to server' : 'Connecting to server...'}
            </div>

            <div>
              <input 
                style={styles.input}
                type="text" 
                placeholder="Enter your name..." 
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                maxLength={20}
                onFocus={(e) => e.target.style.borderColor = currentTheme.accent}
                onBlur={(e) => e.target.style.borderColor = currentTheme.border}
              />
              {mode === 'join' && (
                <input 
                  style={styles.input}
                  type="text" 
                  placeholder="Room ID..." 
                  value={roomId}
                  onChange={(e) => setRoomId(e.target.value.toUpperCase())}
                  maxLength={6}
                  onFocus={(e) => e.target.style.borderColor = currentTheme.accent}
                  onBlur={(e) => e.target.style.borderColor = currentTheme.border}
                />
              )}
            </div>
            
            <div>
              <button 
                style={styles.button}
                className="button"
                onClick={() => {
                  setMode('create');
                  if (playerName.trim()) createRoom();
                }}
                disabled={!playerName.trim() || !connected}
              >
                ➕ Create Room
              </button>
              <button 
                style={{...styles.button, ...styles.buttonSecondary}}
                className="button"
                onClick={() => {
                  if (mode === 'join') {
                    if (playerName.trim() && roomId.trim()) joinRoom();
                  } else {
                    setMode('join');
                  }
                }}
              >
                🚪 {mode === 'join' ? 'Join Room' : 'Join Existing'}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'waiting') {
    return (
      <div style={styles.app}>
        <div 
          style={styles.themeToggle} 
          className="theme-toggle"
          onClick={toggleTheme}
        >
          {theme === 'dark' ? '🌞' : '🌙'}
        </div>
        
        <div style={styles.container}>
          <div style={styles.lobby}>
            <h1 style={{...styles.title, fontSize: '4em'}}>🏠 Room: {room?.id}</h1>
            <p style={styles.subtitle}>Share this room ID with your friends!</p>
            
            <div style={{ marginBottom: '40px' }}>
              <h3 style={{ marginBottom: '20px', fontSize: '1.5em' }}>
                Players ({room?.players?.length || 0}/4):
              </h3>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px', justifyContent: 'center' }}>
                {room?.players?.map((roomPlayer, index) => (
                  <div key={index} style={{
                    padding: '15px 25px',
                    background: roomPlayer.id === player?.id ? 
                      'linear-gradient(135deg, #ff6b6b, #ee5a52)' : 
                      currentTheme.cardBg,
                    borderRadius: '20px',
                    fontSize: '1.2em',
                    fontWeight: '600',
                    border: `2px solid ${roomPlayer.id === player?.id ? '#ff5252' : currentTheme.border}`,
                    backdropFilter: 'blur(10px)',
                    boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)',
                    color: roomPlayer.id === player?.id ? 'white' : currentTheme.text
                  }}>
                    {roomPlayer.name} {roomPlayer.id === player?.id && '(You)'}
                  </div>
                ))}
              </div>
            </div>

            <div style={{
              fontSize: '1.3em', 
              opacity: 0.9,
              padding: '20px',
              background: currentTheme.cardBg,
              borderRadius: '16px',
              border: `1px solid ${currentTheme.border}`,
              backdropFilter: 'blur(10px)'
            }}>
              {room?.players?.length < 2 ? 
                `⏳ Waiting for more players... (${room?.players?.length}/4)` :
                '⚡ Game starting soon! Get ready for UNO!'
              }
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'playing') {
    return (
      <div style={styles.app}>
        {/* Theme Toggle */}
        <div 
          style={styles.themeToggle} 
          className="theme-toggle"
          onClick={toggleTheme}
        >
          {theme === 'dark' ? '🌞' : '🌙'}
        </div>

        {/* Chat Toggle */}
        <button 
          style={styles.chatToggle}
          onClick={() => setShowChat(!showChat)}
        >
          💬
        </button>

        {/* Chat Container */}
        <div style={styles.chatContainer}>
          <div style={styles.chatHeader}>
            💬 Game Chat
          </div>
          <div style={styles.chatMessages}>
            {chatMessages.length === 0 ? (
              <div style={{ textAlign: 'center', opacity: 0.6, padding: '20px' }}>
                No messages yet. Start chatting!
              </div>
            ) : (
              chatMessages.map((msg, index) => (
                <div key={index} style={styles.chatMessage}>
                  <div style={{ fontWeight: '600', fontSize: '0.9em', marginBottom: '4px' }}>
                    {msg.player}
                  </div>
                  <div style={{ fontSize: '0.85em' }}>{msg.message}</div>
                  <div style={{ fontSize: '0.7em', opacity: 0.7, marginTop: '4px' }}>
                    {msg.timestamp}
                  </div>
                </div>
              ))
            )}
          </div>
          <div style={styles.chatInput}>
            <input 
              style={styles.chatInputField}
              type="text"
              placeholder="Type a message..."
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && sendChatMessage()}
              maxLength={100}
            />
            <button style={styles.chatSendBtn} onClick={sendChatMessage}>
              ➤
            </button>
          </div>
        </div>

        <div style={styles.container}>
          <div style={styles.gameBoard}>
            {/* Game Header */}
            <div style={styles.gameHeader}>
              <div style={styles.players}>
                {gameData?.players?.map((playerData, index) => (
                  <div 
                    key={index} 
                    style={{
                      ...styles.player,
                      ...(playerData.isCurrentPlayer ? styles.currentPlayer : {})
                    }}
                  >
                    <div style={{ fontWeight: '700', fontSize: '1.1em' }}>
                      {playerData.name}
                    </div>
                    <div style={{ fontSize: '0.9em', opacity: 0.8 }}>
                      🃏 {playerData.cardCount} cards
                    </div>
                  </div>
                ))}
              </div>
              <div style={{ fontSize: '1.2em', fontWeight: '600' }}>
                Room: {gameData?.roomId}
              </div>
            </div>

            {/* Game Center */}
            <div style={styles.centerArea}>
              <div style={{ textAlign: 'center' }}>
                <div 
                  style={{
                    ...styles.card, 
                    background: 'linear-gradient(135deg, #6c757d, #495057)',
                    position: 'relative'
                  }}
                  className="card-hover"
                  onClick={drawCard}
                >
                  <div style={styles.cardShine} />
                  🃏
                </div>
                <div style={{ marginTop: '15px', fontSize: '1.2em', fontWeight: '600' }}>
                  Draw ({gameData?.deckSize || 0})
                </div>
              </div>

              <div style={{ textAlign: 'center' }}>
                <div 
                  style={{
                    ...styles.card, 
                    background: getCardColor(gameData?.topCard?.color),
                    position: 'relative'
                  }}
                  className="card-hover"
                >
                  <div style={styles.cardShine} />
                  {gameData?.topCard?.value?.toUpperCase() || '?'}
                </div>
                <div style={{ marginTop: '15px', fontSize: '1.2em', fontWeight: '600' }}>
                  Current Color: {gameData?.currentColor?.toUpperCase() || 'Unknown'}
                </div>
              </div>
            </div>

            {/* Turn Indicator */}
            <div style={{
              ...styles.turnIndicator,
              ...(isMyTurn ? styles.myTurn : {})
            }}>
              {isMyTurn ? (
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '15px' }}>
                  ⚡ Your Turn! ⚡
                </div>
              ) : (
                <div>
                  ⏳ Waiting for {gameData?.currentPlayerName || 'player'}...
                </div>
              )}
            </div>
          </div>

          {/* Player Hand */}
          <div style={styles.playerHand}>
            {myCards.length > 0 ? (
              myCards.map((card) => (
                <div
                  key={card.id}
                  style={{
                    ...styles.handCard,
                    background: getCardColor(card.color),
                    position: 'relative'
                  }}
                  className="hand-card"
                  onClick={() => handleCardClick(card)}
                >
                  <div style={styles.cardShine} />
                  {card.value?.toUpperCase()}
                </div>
              ))
            ) : (
              <div style={{ 
                padding: '30px', 
                opacity: 0.7, 
                fontSize: '1.1em',
                background: currentTheme.cardBg,
                borderRadius: '16px',
                border: `1px solid ${currentTheme.border}`
              }}>
                🎴 No cards in hand...
              </div>
            )}
          </div>
        </div>

        {/* Color Picker Modal */}
        {showColorPicker && (
          <div style={styles.colorPicker}>
            <h3 style={{ marginBottom: '25px', fontSize: '1.8em' }}>
              🌈 Choose a Color:
            </h3>
            <div>
              {['red', 'blue', 'green', 'yellow'].map((color) => (
                <div
                  key={color}
                  style={{
                    ...styles.colorOption,
                    background: getCardColor(color)
                  }}
                  onClick={() => handleColorChoice(color)}
                  onMouseEnter={(e) => e.target.style.transform = 'scale(1.2)'}
                  onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
                >
                  {color.charAt(0).toUpperCase()}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  }

  return (
    <div style={styles.app}>
      <div style={styles.container}>
        <div style={{ textAlign: 'center', padding: '50px' }}>
          <h2 style={{ fontSize: '2.5em', marginBottom: '20px' }}>
            🎮 Loading UNO Game...
          </h2>
          <p style={{ fontSize: '1.2em', opacity: 0.8 }}>
            Current state: {gameState}
          </p>
          <div style={{ 
            marginTop: '30px',
            padding: '20px',
            background: currentTheme.cardBg,
            borderRadius: '16px',
            backdropFilter: 'blur(10px)'
          }}>
            Please wait while we set up your game...
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
