import React, { useState, useEffect, useCallback } from 'react';
import styled, { ThemeProvider, createGlobalStyle } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { Toaster, toast } from 'react-hot-toast';
import { Users, Plus, LogIn, Moon, Sun, Target } from 'lucide-react';
import io from 'socket.io-client';

// Global Styles (same as before...)
const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
  body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
    background: linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%);
    color: white;
    overflow-x: hidden;
  }
`;

// Styled Components (keeping the main ones, adding game-specific ones)
const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
`;

// Game Interface
const GameInterface = styled(motion.div)`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const GameHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
`;

const PlayerCard = styled(motion.div)`
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 15px 20px;
  background: ${props => props.isCurrentPlayer ? 
    'linear-gradient(135deg, #ffd700, #ff6b6b)' : 
    'rgba(255, 255, 255, 0.15)'
  };
  border-radius: 20px;
  backdrop-filter: blur(15px);
  border: ${props => props.isCurrentPlayer ? '3px solid #ffd700' : '1px solid rgba(255, 255, 255, 0.3)'};
  
  img {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    border: 3px solid rgba(255, 255, 255, 0.5);
  }
  
  .player-info {
    .player-name {
      font-weight: 700;
      font-size: 1em;
    }
    .card-count {
      font-size: 0.85em;
      opacity: 0.9;
    }
  }
`;

const GameBoard = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 30px;
  position: relative;
`;

const CenterArea = styled.div`
  display: flex;
  align-items: center;
  gap: 60px;
  margin-bottom: 40px;
`;

const Card = styled(motion.div)`
  width: 120px;
  height: 180px;
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.8em;
  font-weight: bold;
  cursor: ${props => props.clickable ? 'pointer' : 'default'};
  position: relative;
  box-shadow: 0 12px 35px rgba(0, 0, 0, 0.4);
  border: 4px solid rgba(255, 255, 255, 0.4);
  
  background: ${props => {
    if (props.cardColor === 'red') return 'linear-gradient(135deg, #ff4757, #c44569)';
    if (props.cardColor === 'blue') return 'linear-gradient(135deg, #3742fa, #2f3542)';
    if (props.cardColor === 'green') return 'linear-gradient(135deg, #2ed573, #1e90ff)';
    if (props.cardColor === 'yellow') return 'linear-gradient(135deg, #ffa502, #ff6348)';
    if (props.cardColor === 'wild') return 'linear-gradient(135deg, #8e44ad, #e74c3c, #f39c12, #27ae60)';
    return 'linear-gradient(135deg, #57606f, #2f3542)';
  }};
  
  color: white;
  text-shadow: 3px 3px 6px rgba(0, 0, 0, 0.7);
  
  &:hover {
    transform: ${props => props.clickable ? 'translateY(-15px) scale(1.1)' : 'translateY(-5px)'};
    box-shadow: 0 20px 50px rgba(0, 0, 0, 0.5);
  }
`;

const PlayerHand = styled.div`
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 12px;
  padding: 25px;
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(25px);
  border-radius: 25px;
  max-width: 95vw;
  overflow-x: auto;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.3);
`;

const GameStatus = styled.div`
  text-align: center;
  margin-top: 25px;
  padding: 20px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(15px);
  border-radius: 20px;
  
  .turn-indicator {
    font-size: 1.4em;
    font-weight: 700;
    margin-bottom: 10px;
    color: ${props => props.isMyTurn ? '#2ed573' : 'white'};
  }
  
  .penalty-warning {
    color: #ff4757;
    font-weight: 600;
    margin-top: 8px;
  }
`;

const ActionButton = styled(motion.button)`
  padding: 15px 30px;
  border: none;
  border-radius: 15px;
  font-size: 1.1em;
  font-weight: 700;
  cursor: pointer;
  backdrop-filter: blur(15px);
  border: 2px solid rgba(255, 255, 255, 0.3);
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 10px;
  background: linear-gradient(135deg, #2ed573, #1e90ff);
  color: white;
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
  
  &:hover:not(:disabled) {
    transform: translateY(-3px) scale(1.05);
  }
`;

const ColorPicker = styled(motion.div)`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(30px);
  padding: 40px;
  border-radius: 25px;
  z-index: 1000;
  text-align: center;
  
  h3 {
    margin-bottom: 25px;
    color: #333;
    font-size: 1.5em;
  }
`;

const ColorOption = styled(motion.div)`
  width: 90px;
  height: 90px;
  border-radius: 50%;
  cursor: pointer;
  margin: 15px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-size: 1.3em;
  color: white;
  font-weight: bold;
  border: 4px solid rgba(255, 255, 255, 0.8);
  
  &:hover {
    transform: scale(1.15);
    border-color: #ffd700;
  }
`;

// Rest of the lobby components (same as before, keeping them short for space)
const GameContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  padding: 20px;
`;

const GameCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 24px;
  padding: 50px;
  max-width: 600px;
  width: 100%;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 4em;
  font-weight: 700;
  margin-bottom: 10px;
  background: linear-gradient(135deg, #ff4757, #ffd700);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Subtitle = styled.p`
  font-size: 1.3em;
  opacity: 0.8;
  margin-bottom: 40px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-bottom: 30px;
`;

const Input = styled.input`
  padding: 18px 24px;
  border: 2px solid rgba(255, 255, 255, 0.2);
  border-radius: 16px;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  font-size: 1.2em;
  
  &::placeholder {
    color: rgba(255, 255, 255, 0.6);
  }
  
  &:focus {
    outline: none;
    border-color: #ff4757;
    background: rgba(255, 255, 255, 0.15);
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 30px;
`;

const Button = styled(motion.button)`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  padding: 18px 32px;
  border: none;
  border-radius: 16px;
  font-size: 1.2em;
  font-weight: 600;
  cursor: pointer;
  
  &.primary {
    background: linear-gradient(135deg, #ff4757, #ff3742);
    color: white;
  }
  
  &.secondary {
    background: linear-gradient(135deg, #5352ed, #3742fa);
    color: white;
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const ThemeToggle = styled(motion.button)`
  position: absolute;
  top: 30px;
  right: 30px;
  width: 60px;
  height: 60px;
  border: none;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ConnectionStatus = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  font-size: 1.2em;
  margin-bottom: 30px;
  
  .status-dot {
    width: 16px;
    height: 16px;
    border-radius: 50%;
    background: ${props => props.connected ? '#2ed573' : '#ff4757'};
  }
`;

const RoomInfo = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 40px;
  margin-top: 30px;
`;

const PlayerList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 15px;
  justify-content: center;
  margin-top: 25px;
`;

const PlayerChip = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 15px 20px;
  background: ${props => props.isMe ? 
    'linear-gradient(135deg, #ff4757, #ff3742)' : 
    'rgba(255, 255, 255, 0.15)'
  };
  border-radius: 30px;
  font-size: 1em;
  font-weight: 500;
  
  img {
    width: 32px;
    height: 32px;
    border-radius: 50%;
  }
`;

function App() {
  const [gameState, setGameState] = useState('lobby');
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);
  const [player, setPlayer] = useState(null);
  const [room, setRoom] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [mode, setMode] = useState('create');
  
  // Game state
  const [gameData, setGameData] = useState(null);
  const [myCards, setMyCards] = useState([]);
  const [isMyTurn, setIsMyTurn] = useState(false);
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [pendingWildCard, setPendingWildCard] = useState(null);

  // Socket connection
  useEffect(() => {
    const SERVER_URL = 'http://localhost:3002';
    const socketInstance = io(SERVER_URL);

    socketInstance.on('connect', () => {
      setConnected(true);
      toast.success('ðŸŽ® Connected to UNO server!');
    });

    socketInstance.on('disconnect', () => {
      setConnected(false);
      toast.error('âŒ Disconnected');
    });

    socketInstance.on('roomCreated', (data) => {
      setRoom(data.room);
      setPlayer(data.room.players[0]);
      setGameState('waiting');
      toast.success(`ðŸ  Room ${data.roomId} created!`);
    });

    socketInstance.on('roomJoined', (data) => {
      setRoom(data.room);
      setPlayer(data.player);
      setGameState('waiting');
      toast.success('ðŸšª Joined room!');
    });

    socketInstance.on('playerJoined', (data) => {
      setRoom(prevRoom => ({
        ...prevRoom,
        players: [...(prevRoom?.players || []), data.player]
      }));
      toast.success(`${data.player.name} joined!`);
    });

    socketInstance.on('gameStarted', (data) => {
      setGameState('playing');
      toast.success('ðŸŽ® Cards dealt! Game started!');
    });

    socketInstance.on('gameUpdate', (data) => {
      setGameData(data);
      setMyCards(data.myCards || []);
      setIsMyTurn(data.isMyTurn || false);
    });

    socketInstance.on('gameError', (error) => {
      toast.error(error.message);
    });

    socketInstance.on('unoAlert', (data) => {
      toast.success(data.message);
    });

    socketInstance.on('gameWon', (data) => {
      toast.success(data.message);
      setTimeout(() => {
        setGameState('lobby');
        setRoom(null);
        setPlayer(null);
        setGameData(null);
      }, 5000);
    });

    socketInstance.on('error', (error) => {
      toast.error(error.message);
    });

    setSocket(socketInstance);

    return () => socketInstance.close();
  }, []);

  const createRoom = useCallback(() => {
    if (socket && playerName.trim()) {
      socket.emit('createRoom', { playerName: playerName.trim() });
    }
  }, [socket, playerName]);

  const joinRoom = useCallback(() => {
    if (socket && playerName.trim() && roomId.trim()) {
      socket.emit('joinRoom', { 
        roomId: roomId.trim().toUpperCase(), 
        playerName: playerName.trim() 
      });
    }
  }, [socket, playerName, roomId]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (mode === 'create') {
      createRoom();
    } else {
      joinRoom();
    }
  };

  const playCard = useCallback((cardId, chosenColor = null) => {
    if (socket) {
      socket.emit('playCard', { cardId, chosenColor });
    }
  }, [socket]);

  const drawCard = useCallback(() => {
    if (socket) {
      socket.emit('drawCard');
    }
  }, [socket]);

  const handleCardClick = (card) => {
    if (!isMyTurn) {
      toast.error('Not your turn!');
      return;
    }

    if (card.color === 'wild') {
      setPendingWildCard(card);
      setShowColorPicker(true);
    } else {
      playCard(card.id);
    }
  };

  const handleColorChoice = (color) => {
    if (pendingWildCard) {
      playCard(pendingWildCard.id, color);
      setPendingWildCard(null);
      setShowColorPicker(false);
    }
  };

  const renderGameInterface = () => (
    <GameInterface>
      {/* Game Header */}
      <GameHeader>
        <div style={{ display: 'flex', gap: '20px' }}>
          {gameData?.players?.map((playerData, index) => (
            <PlayerCard
              key={playerData.id}
              isCurrentPlayer={index === gameData.currentPlayer}
            >
              <img src={playerData.avatar} alt={playerData.name} />
              <div className="player-info">
                <div className="player-name">{playerData.name}</div>
                <div className="card-count">{playerData.cardCount} cards</div>
              </div>
            </PlayerCard>
          ))}
        </div>
      </GameHeader>

      {/* Main Game Area */}
      <GameBoard>
        <CenterArea>
          {/* Draw Pile */}
          <div style={{ textAlign: 'center' }}>
            <Card
              cardColor="deck"
              clickable={isMyTurn}
              onClick={drawCard}
              whileHover={{ scale: isMyTurn ? 1.05 : 1 }}
            >
              ðŸƒ
            </Card>
            <div style={{ marginTop: '10px', fontSize: '1.1em' }}>
              Draw ({gameData?.deckSize || 0})
            </div>
          </div>

          {/* Current Card */}
          <div style={{ textAlign: 'center' }}>
            <Card
              cardColor={gameData?.currentColor || gameData?.topCard?.color}
              value={gameData?.topCard?.value}
            >
              {gameData?.topCard?.value?.toUpperCase() || '?'}
            </Card>
            <div style={{ marginTop: '10px', fontSize: '1.1em' }}>
              Color: {gameData?.currentColor?.toUpperCase() || 'Unknown'}
            </div>
          </div>
        </CenterArea>

        <GameStatus isMyTurn={isMyTurn}>
          <div className="turn-indicator">
            {isMyTurn ? 'ðŸŽ¯ Your Turn!' : `Waiting for ${gameData?.currentPlayerName}...`}
          </div>
          
          {gameData?.drawCount > 0 && (
            <div className="penalty-warning">
              âš¡ Draw {gameData.drawCount} cards penalty!
            </div>
          )}
        </GameStatus>
      </GameBoard>

      {/* Player Hand */}
      <PlayerHand>
        {myCards.map((card) => (
          <Card
            key={card.id}
            cardColor={card.color}
            value={card.value}
            clickable={isMyTurn}
            onClick={() => handleCardClick(card)}
            whileHover={{ 
              y: isMyTurn ? -20 : -5, 
              scale: isMyTurn ? 1.15 : 1.05
            }}
            style={{ minWidth: '100px' }}
          >
            {card.value.toUpperCase()}
          </Card>
        ))}
      </PlayerHand>

      {/* Color Picker */}
      <AnimatePresence>
        {showColorPicker && (
          <ColorPicker
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
          >
            <h3>Choose a Color:</h3>
            {['red', 'blue', 'green', 'yellow'].map((color) => (
              <ColorOption
                key={color}
                style={{
                  background: color === 'red' ? '#ff4757' :
                             color === 'blue' ? '#3742fa' :
                             color === 'green' ? '#2ed573' : '#ffa502'
                }}
                onClick={() => handleColorChoice(color)}
                whileHover={{ scale: 1.15 }}
                whileTap={{ scale: 0.9 }}
              >
                {color.toUpperCase()}
              </ColorOption>
            ))}
          </ColorPicker>
        )}
      </AnimatePresence>
    </GameInterface>
  );

  return (
    <ThemeProvider theme={{}}>
      <GlobalStyle />
      <AppContainer>
        <ThemeToggle>
          <Sun size={24} />
        </ThemeToggle>

        <AnimatePresence mode="wait">
          {gameState === 'lobby' && (
            <GameContainer key="lobby">
              <GameCard>
                <Title>ðŸƒ UNO</Title>
                <Subtitle>Real-time Multiplayer Card Game</Subtitle>

                <ConnectionStatus connected={connected}>
                  <div className="status-dot" />
                  {connected ? 'Connected to server' : 'Connecting...'}
                </ConnectionStatus>

                <Form onSubmit={handleSubmit}>
                  <Input
                    type="text"
                    placeholder="Enter your name..."
                    value={playerName}
                    onChange={(e) => setPlayerName(e.target.value)}
                    required
                  />

                  {mode === 'join' && (
                    <Input
                      type="text"
                      placeholder="Room ID..."
                      value={roomId}
                      onChange={(e) => setRoomId(e.target.value.toUpperCase())}
                      required
                    />
                  )}
                </Form>

                <ButtonGroup>
                  <Button
                    className="primary"
                    onClick={() => {
                      setMode('create');
                      if (playerName.trim()) createRoom();
                    }}
                    disabled={!playerName.trim() || !connected}
                  >
                    <Plus size={20} />
                    Create Room
                  </Button>
                  
                  <Button
                    className="secondary"
                    onClick={() => {
                      if (mode === 'join') {
                        if (playerName.trim() && roomId.trim()) joinRoom();
                      } else {
                        setMode('join');
                      }
                    }}
                    disabled={mode === 'join' && (!playerName.trim() || !roomId.trim()) || !connected}
                  >
                    <LogIn size={20} />
                    {mode === 'join' ? 'Join Room' : 'Join Existing'}
                  </Button>
                </ButtonGroup>
              </GameCard>
            </GameContainer>
          )}

          {gameState === 'waiting' && room && (
            <GameContainer key="waiting">
              <GameCard>
                <Title>ðŸ  Room: {room.id}</Title>
                <Subtitle>Share this room ID with your friends!</Subtitle>

                <RoomInfo>
                  <PlayerList>
                    {room.players?.map((roomPlayer) => (
                      <PlayerChip
                        key={roomPlayer.id}
                        isMe={roomPlayer.id === player?.id}
                      >
                        <img src={roomPlayer.avatar} alt={roomPlayer.name} />
                        {roomPlayer.name}
                        {roomPlayer.id === player?.id && ' (You)'}
                      </PlayerChip>
                    ))}
                  </PlayerList>

                  <p style={{ 
                    marginTop: '30px', 
                    opacity: 0.8, 
                    fontSize: '1.1em',
                    textAlign: 'center'
                  }}>
                    {room.players?.length < 2 ? (
                      <>
                        <Users size={20} style={{ marginRight: '10px' }} />
                        Waiting for more players... ({room.players?.length}/4)
                      </>
                    ) : (
                      'âš¡ Game starting soon with real UNO cards!'
                    )}
                  </p>
                </RoomInfo>
              </GameCard>
            </GameContainer>
          )}

          {gameState === 'playing' && gameData && renderGameInterface()}
        </AnimatePresence>
        
        <Toaster position="top-center" />
      </AppContainer>
    </ThemeProvider>
  );
}

export default App;
