import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

function App() {
  const [gameState, setGameState] = useState('lobby');
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);
  const [player, setPlayer] = useState(null);
  const [room, setRoom] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [mode, setMode] = useState('create');
  
  // Game state
  const [gameData, setGameData] = useState(null);
  const [myCards, setMyCards] = useState([]);
  const [isMyTurn, setIsMyTurn] = useState(false);

  // Socket connection
  useEffect(() => {
    console.log('üîó Connecting to server...');
    const socketInstance = io('http://localhost:3002', {
      transports: ['websocket', 'polling']
    });

    socketInstance.on('connect', () => {
      setConnected(true);
      console.log('‚úÖ Connected to server');
      alert('‚úÖ Connected to UNO server!');
    });

    socketInstance.on('disconnect', () => {
      setConnected(false);
      console.log('‚ùå Disconnected');
      alert('‚ùå Disconnected from server');
    });

    socketInstance.on('roomCreated', (data) => {
      console.log('üè† Room created:', data);
      setRoom(data.room);
      setPlayer(data.room.players[0]);
      setGameState('waiting');
      alert(`üè† Room ${data.roomId} created!`);
    });

    socketInstance.on('roomJoined', (data) => {
      console.log('üö™ Room joined:', data);
      setRoom(data.room);
      setPlayer(data.player);
      setGameState('waiting');
      alert('üö™ Joined room successfully!');
    });

    socketInstance.on('playerJoined', (data) => {
      console.log('üë§ Player joined:', data);
      setRoom(prevRoom => ({
        ...prevRoom,
        players: [...(prevRoom?.players || []), data.player]
      }));
      alert(`${data.player.name} joined the game!`);
    });

    socketInstance.on('gameStarted', (data) => {
      console.log('üéÆ Game started:', data);
      setGameState('playing');
      alert('üéÆ Game Started! Cards dealt!');
    });

    socketInstance.on('gameUpdate', (data) => {
      console.log('üìä Game update:', data);
      setGameData(data);
      setMyCards(data.myCards || []);
      setIsMyTurn(data.isMyTurn || false);
    });

    socketInstance.on('gameError', (error) => {
      console.error('‚ùå Game error:', error);
      alert(`‚ùå ${error.message}`);
    });

    socketInstance.on('unoAlert', (data) => {
      console.log('üö® UNO Alert:', data);
      alert(data.message);
    });

    socketInstance.on('gameWon', (data) => {
      console.log('üèÜ Game won:', data);
      alert(data.message);
      setTimeout(() => {
        setGameState('lobby');
        setRoom(null);
        setPlayer(null);
        setGameData(null);
      }, 3000);
    });

    socketInstance.on('error', (error) => {
      console.error('‚ùå Socket error:', error);
      alert(`‚ùå ${error.message}`);
    });

    setSocket(socketInstance);

    return () => {
      console.log('üîå Closing socket connection');
      socketInstance.close();
    };
  }, []);

  const createRoom = () => {
    if (socket && playerName.trim()) {
      console.log('üéØ Creating room for:', playerName);
      socket.emit('createRoom', { playerName: playerName.trim() });
    } else {
      alert('Please enter your name and make sure you\'re connected!');
    }
  };

  const joinRoom = () => {
    if (socket && playerName.trim() && roomId.trim()) {
      console.log('üéØ Joining room:', roomId, 'as:', playerName);
      socket.emit('joinRoom', { 
        roomId: roomId.trim().toUpperCase(), 
        playerName: playerName.trim() 
      });
    } else {
      alert('Please enter your name and room ID!');
    }
  };

  const playCard = (cardId) => {
    if (socket && isMyTurn) {
      console.log('üÉè Playing card:', cardId);
      socket.emit('playCard', { cardId });
    } else {
      alert('Not your turn or not connected!');
    }
  };

  const drawCard = () => {
    if (socket && isMyTurn) {
      console.log('üÉè Drawing card');
      socket.emit('drawCard');
    } else {
      alert('Not your turn or not connected!');
    }
  };

  // Render functions
  const renderLobby = () => (
    <div style={{
      background: 'linear-gradient(135deg, #1a1a2e, #16213e, #0f3460)',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        padding: '50px',
        borderRadius: '20px',
        textAlign: 'center',
        minWidth: '400px',
        backdropFilter: 'blur(10px)'
      }}>
        <h1 style={{ fontSize: '4em', marginBottom: '20px' }}>üÉè UNO</h1>
        <p style={{ fontSize: '1.2em', marginBottom: '30px' }}>Real-time Multiplayer Card Game</p>
        
        <div style={{ marginBottom: '20px' }}>
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center', 
            gap: '10px',
            marginBottom: '20px'
          }}>
            <div style={{
              width: '12px',
              height: '12px',
              borderRadius: '50%',
              background: connected ? '#2ed573' : '#ff4757'
            }}></div>
            {connected ? 'Connected to server' : 'Connecting...'}
          </div>
        </div>

        <input
          type="text"
          placeholder="Enter your name..."
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          style={{
            width: '100%',
            padding: '15px',
            marginBottom: '15px',
            borderRadius: '10px',
            border: 'none',
            fontSize: '1.1em',
            background: 'rgba(255, 255, 255, 0.2)',
            color: 'white'
          }}
        />

        {mode === 'join' && (
          <input
            type="text"
            placeholder="Room ID..."
            value={roomId}
            onChange={(e) => setRoomId(e.target.value.toUpperCase())}
            style={{
              width: '100%',
              padding: '15px',
              marginBottom: '15px',
              borderRadius: '10px',
              border: 'none',
              fontSize: '1.1em',
              background: 'rgba(255, 255, 255, 0.2)',
              color: 'white'
            }}
          />
        )}

        <div style={{ display: 'flex', gap: '15px', marginBottom: '20px' }}>
          <button
            onClick={() => {
              setMode('create');
              if (playerName.trim()) createRoom();
            }}
            disabled={!playerName.trim() || !connected}
            style={{
              flex: 1,
              padding: '15px',
              borderRadius: '10px',
              border: 'none',
              fontSize: '1.1em',
              background: '#ff4757',
              color: 'white',
              cursor: 'pointer'
            }}
          >
            ‚ûï Create Room
          </button>
          
          <button
            onClick={() => {
              if (mode === 'join') {
                if (playerName.trim() && roomId.trim()) joinRoom();
              } else {
                setMode('join');
              }
            }}
            disabled={mode === 'join' && (!playerName.trim() || !roomId.trim()) || !connected}
            style={{
              flex: 1,
              padding: '15px',
              borderRadius: '10px',
              border: 'none',
              fontSize: '1.1em',
              background: '#5352ed',
              color: 'white',
              cursor: 'pointer'
            }}
          >
            üö™ {mode === 'join' ? 'Join Room' : 'Join Existing'}
          </button>
        </div>
      </div>
    </div>
  );

  const renderWaiting = () => (
    <div style={{
      background: 'linear-gradient(135deg, #1a1a2e, #16213e, #0f3460)',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        padding: '50px',
        borderRadius: '20px',
        textAlign: 'center',
        minWidth: '500px'
      }}>
        <h1 style={{ fontSize: '3em', marginBottom: '20px' }}>üè† Room: {room?.id}</h1>
        <p style={{ fontSize: '1.2em', marginBottom: '30px' }}>Share this room ID with your friends!</p>
        
        <div style={{ marginBottom: '30px' }}>
          <h3>Players ({room?.players?.length || 0}/4):</h3>
          {room?.players?.map((roomPlayer, index) => (
            <div key={index} style={{
              display: 'inline-block',
              margin: '10px',
              padding: '10px 15px',
              background: roomPlayer.id === player?.id ? '#ff4757' : 'rgba(255, 255, 255, 0.2)',
              borderRadius: '20px',
              fontSize: '1.1em'
            }}>
              {roomPlayer.name} {roomPlayer.id === player?.id && '(You)'}
            </div>
          ))}
        </div>

        <p style={{ fontSize: '1.1em', opacity: 0.8 }}>
          {room?.players?.length < 2 ? 
            `Waiting for more players... (${room?.players?.length}/4)` :
            '‚ö° Game starting soon!'
          }
        </p>
      </div>
    </div>
  );

  const renderGame = () => (
    <div style={{
      background: 'linear-gradient(135deg, #1a1a2e, #16213e, #0f3460)',
      minHeight: '100vh',
      color: 'white',
      fontFamily: 'Arial, sans-serif',
      padding: '20px'
    }}>
      {/* Header */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '30px',
        padding: '20px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '15px'
      }}>
        <div style={{ display: 'flex', gap: '20px' }}>
          {gameData?.players?.map((playerData, index) => (
            <div key={playerData.id} style={{
              padding: '10px 15px',
              background: index === gameData.currentPlayer ? '#ffd700' : 'rgba(255, 255, 255, 0.2)',
              borderRadius: '10px',
              color: index === gameData.currentPlayer ? '#000' : '#fff'
            }}>
              <div style={{ fontWeight: 'bold' }}>{playerData.name}</div>
              <div>{playerData.cardCount} cards</div>
            </div>
          ))}
        </div>
        <div>Room: {gameData?.roomId}</div>
      </div>

      {/* Game Board */}
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '40px', marginBottom: '30px' }}>
          {/* Draw Pile */}
          <div style={{ textAlign: 'center' }}>
            <div
              onClick={drawCard}
              style={{
                width: '120px',
                height: '180px',
                background: '#57606f',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '2em',
                cursor: isMyTurn ? 'pointer' : 'default',
                border: '3px solid #fff',
                marginBottom: '10px'
              }}
            >
              üÉè
            </div>
            <div>Draw ({gameData?.deckSize || 0})</div>
          </div>

          {/* Current Card */}
          <div style={{ textAlign: 'center' }}>
            <div style={{
              width: '120px',
              height: '180px',
              background: gameData?.topCard?.color === 'red' ? '#ff4757' :
                         gameData?.topCard?.color === 'blue' ? '#3742fa' :
                         gameData?.topCard?.color === 'green' ? '#2ed573' :
                         gameData?.topCard?.color === 'yellow' ? '#ffa502' : '#8e44ad',
              borderRadius: '15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '1.5em',
              fontWeight: 'bold',
              color: 'white',
              border: '3px solid #fff',
              marginBottom: '10px'
            }}>
              {gameData?.topCard?.value?.toUpperCase() || '?'}
            </div>
            <div>Current: {gameData?.currentColor?.toUpperCase()}</div>
          </div>
        </div>

        {/* Turn Indicator */}
        <div style={{
          padding: '20px',
          background: isMyTurn ? '#2ed573' : 'rgba(255, 255, 255, 0.1)',
          borderRadius: '15px',
          fontSize: '1.5em',
          fontWeight: 'bold',
          marginBottom: '30px'
        }}>
          {isMyTurn ? 'üéØ Your Turn!' : `Waiting for ${gameData?.currentPlayerName}...`}
        </div>
      </div>

      {/* Player Hand */}
      <div style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        gap: '10px',
        padding: '20px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '15px',
        maxWidth: '90vw',
        overflowX: 'auto'
      }}>
        {myCards.map((card) => (
          <div
            key={card.id}
            onClick={() => playCard(card.id)}
            style={{
              width: '80px',
              height: '120px',
              background: card.color === 'red' ? '#ff4757' :
                         card.color === 'blue' ? '#3742fa' :
                         card.color === 'green' ? '#2ed573' :
                         card.color === 'yellow' ? '#ffa502' : '#8e44ad',
              borderRadius: '10px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '1em',
              fontWeight: 'bold',
              color: 'white',
              cursor: isMyTurn ? 'pointer' : 'default',
              border: '2px solid #fff',
              minWidth: '80px'
            }}
          >
            {card.value.toUpperCase()}
          </div>
        ))}
      </div>
    </div>
  );

  console.log('üéÆ Current game state:', gameState);
  console.log('üîó Connected:', connected);
  console.log('üë§ Player:', player);
  console.log('üè† Room:', room);
  console.log('üéØ Game data:', gameData);

  if (gameState === 'lobby') return renderLobby();
  if (gameState === 'waiting') return renderWaiting();
  if (gameState === 'playing') return renderGame();

  return (
    <div style={{
      background: 'linear-gradient(135deg, #1a1a2e, #16213e, #0f3460)',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontSize: '2em'
    }}>
      üéÆ Loading UNO Game...
    </div>
  );
}

export default App;
